import { Component } from '@angular/core';
import { PatientService } from '../patient.service';
import { Patient } from '../patient';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-patient',
  templateUrl: './create-patient.component.html',
  styleUrls: ['./create-patient.component.css']
})
export class CreatePatientComponent {

  patient: Patient = new Patient();

  constructor(private patientService: PatientService, private router: Router) {

  }

  savePatient(){
    this.patientService.createPatient(this.patient).subscribe(data => {
      console.log(data);
      this.goToPatientList();
    })
  }

  onSubmit(){
  this.savePatient();
  }

  goToPatientList(){
    this.router.navigate(['/doctordash']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Add New Patient</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <style>
    body {
      background-color: #f0f2f5;
    }
    .navbar {
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    }
    .form-container {
      /* background-color: #fff;
      border-radius: 10px;
      padding: 40px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1); */
      display: flex;
      flex-wrap: wrap;
    }
    .form-group label {
      font-weight: 600;
      color: #333;
    }
    .btn-custom {
      background-color: #28a745;
      border-color: #28a745;
      color: #fff;
    }
    .btn-custom:hover {
      background-color: #218838;
    }
    .btn-reset {
      background-color: #dc3545;
      border-color: #dc3545;
      color: #fff;
    }
    .btn-reset:hover {
      background-color: #c82333;
    }
    .navbar-brand, .nav-link {
      font-size: 1.2rem;
    }
    .container h2 {
      color: #007bff;
      font-weight: bold;
      margin-bottom: 20px;
    }
  </style>
</head>
<body>

<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">WAGH HOSPITALS</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link" href="#">Back</a>
        </li>
      </ul>
      <div class="d-flex">
        <a class="btn btn-danger text-white" href="#">Logout</a>
      </div>
    </div>
  </div>
</nav>

<div class="container mt-5">
    <div class="form-container mx-auto">
    <h2 class="text-center">Add New Patient</h2>
    <form (ngSubmit)="onSubmit()" class="p-4 shadow-lg rounded bg-light">

      <div class="row mb-3">
        <div class="form-group col-md-6">
          <label for="name"><i class="fas fa-user"></i> Name</label>
          <input type="text" class="form-control" id="name" name="name" placeholder="Enter patient's name" [(ngModel)]="patient.name" required>
        </div>
        <div class="form-group col-md-6">
          <label for="age"><i class="fas fa-calendar-alt"></i> Age</label>
          <input type="number" class="form-control" id="age" name="age" placeholder="Enter patient's age" [(ngModel)]="patient.age" required>
        </div>
      </div>

      <div class="row mb-3">
        <div class="form-group col-md-6">
          <label for="mobile"><i class="fas fa-phone"></i> Mobile</label>
          <input type="tel" class="form-control" id="mobile" name="mobile" placeholder="Enter patient's mobile number" [(ngModel)]="patient.mobile" required>
        </div>
        <div class="form-group col-md-6">
          <label for="blood"><i class="fas fa-tint"></i> Blood Group</label>
          <select class="form-control" id="blood" name="blood" [(ngModel)]="patient.blood" required>
            <option value="">Select Blood Group</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
        </div>
      </div>

      <div class="row mb-3">
        <div class="form-group col-md-6">
          <label for="gender"><i class="fas fa-venus-mars"></i> Gender</label>
          <select class="form-control" id="gender" name="gender" [(ngModel)]="patient.gender" required>
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <div class="form-group col-md-6">
          <label for="dose"><i class="fas fa-syringe"></i> Dose</label>
          <select class="form-control" id="dose" name="dose" [(ngModel)]="patient.dose" required>
            <option value="">Select Dose</option>
            <option value="First">First</option>
            <option value="Second">Second</option>
            <option value="Third">Third</option>
            <option value="Other">Other</option>
          </select>
        </div>
      </div>

      <div class="row mb-3">
        <div class="form-group col-md-6">
          <label for="urgency"><i class="fas fa-exclamation-circle"></i> Urgency Level</label>
          <select class="form-control" id="urgency" name="urgency" [(ngModel)]="patient.urgency" required>
            <option value="">Select Urgency Level</option>
            <option value="Normal">Normal</option>
            <option value="High">High</option>
            <option value="Critical">Critical</option>
          </select>
        </div>
        <div class="form-group col-md-6">
          <label for="fees"><i class="fas fa-money-bill"></i> Fees</label>
          <input type="number" class="form-control" id="fees" name="fees" placeholder="Enter consultation fees" [(ngModel)]="patient.fees" required>
        </div>
      </div>

      <div class="row mb-3">
        <div class="form-group col-md-12">
          <label for="prescription"><i class="fas fa-file-prescription"></i> Prescription</label>
          <textarea class="form-control" id="prescription" name="prescription" placeholder="Enter prescription details" [(ngModel)]="patient.prescription" rows="2" required></textarea>
        </div>
      </div>

      <div class="text-center">
        <button type="submit" class="btn btn-custom me-2">Submit</button>
        <button type="reset" class="btn btn-reset">Reset</button>
      </div>

    </form>
  </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
<script src="https://kit.fontawesome.com/a076d05399.js" crossorigin="anonymous"></script>
</body>
</html>
