import { Component } from '@angular/core';
import { Appointment } from '../appointment'; // Adjust path as per your project structure
import { AppointmentService } from '../appointment.service'; // Adjust path as per your project structure
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-appointment',
  templateUrl: './create-appointment.component.html',
  styleUrls: ['./create-appointment.component.css']
})
export class CreateAppointmentComponent {

  appointment: Appointment = new Appointment();

  constructor(private appointmentService: AppointmentService, private router: Router) { }

  onSubmit() {
    this.saveAppointment();
  }

  saveAppointment() {
    this.appointmentService.createAppointment(this.appointment).subscribe(data => {
      console.log(data);
      this.goToAppointmentList();
    });
  }

  goToAppointmentList() {
    this.router.navigate(['/appointmentlist']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Add New Appointment</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <style>
    body {
      background-color: #f0f2f5;
    }
    .navbar {
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    }
    .form-container {
      background-color: #fff;
      border-radius: 10px;
      padding: 40px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    }
    .form-group label {
      font-weight: 600;
      color: #333;
    }
    .btn-custom {
      background-color: #007bff;
      border-color: #007bff;
      color: #fff;
    }
    .btn-custom:hover {
      background-color: #0056b3;
    }
    .btn-reset {
      background-color: #ffc107;
      border-color: #ffc107;
      color: #212529;
    }
    .btn-reset:hover {
      background-color: #e0a800;
    }
    .navbar-brand, .nav-link {
      font-size: 1.2rem;
    }
    .container h2 {
      color: #007bff;
      font-weight: bold;
      margin-bottom: 20px;
    }
  </style>
</head>
<body>

<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">WAGH HOSPITALS</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link" href="#">Back</a>
        </li>
      </ul>
      <div class="d-flex">
        <a class="btn btn-danger text-white" href="#">Logout</a>
      </div>
    </div>
  </div>
</nav>

<div class="container mt-5">
  <div class="form-container mx-auto">
    <h2 class="text-center">Add New Appointment</h2>
    <form (ngSubmit)="onSubmit()">
      <div class="row mb-3">
        <div class="col-md-6">
          <div class="form-group">
            <label for="name">Patient Name</label>
            <input type="text" class="form-control" id="name" name="name" [(ngModel)]="appointment.name" placeholder="Enter patient's name" required>
          </div>
        </div>
        <div class="col-md-6">
          <div class="form-group">
            <label for="doctorName">Doctor Name</label>
            <select class="form-control" id="doctorName" name="doctorName" [(ngModel)]="appointment.doctorName" required>
              <option value="">Select Doctor</option>
              <option value="Dr.Santosh Wagh">Dr.Santosh Wagh</option>
              <option value="Dr.Ghayal Hanumant">Dr.Ghayal Hanumant</option>
              <option value="Dr.Madhuri W">Dr.Madhuri W</option>
              <option value="Dr.Shubham Wagh">Dr.Shubham Wagh</option>
              <option value="Dr.Deepak Deshpande">Dr.Deepak Deshpande</option>
              <option value="Other">Other</option>
            </select>
          </div>
        </div>
      </div>

      <div class="row mb-3">
        <div class="col-md-6">
          <div class="form-group">
            <label for="age">Age</label>
            <input type="number" class="form-control" id="age" name="age" [(ngModel)]="appointment.age" placeholder="Enter age" required>
          </div>
        </div>
        <div class="col-md-6">
          <div class="form-group">
            <label for="mobile">Mobile</label>
            <input type="tel" class="form-control" id="mobile" name="mobile" [(ngModel)]="appointment.mobile" placeholder="Enter mobile number" required>
          </div>
        </div>
      </div>

      <div class="mb-3">
        <div class="form-group">
          <label for="symptoms">Symptoms</label>
          <input type="text" class="form-control" id="symptoms" name="symptoms" [(ngModel)]="appointment.symptoms" placeholder="Enter symptoms" required>
        </div>
      </div>

      <div class="mb-3">
        <div class="form-group">
          <label for="appointmentNumber">Appointment Number</label>
          <input type="text" class="form-control" id="appointmentNumber" name="appointmentNumber" [(ngModel)]="appointment.appointmentNumber" placeholder="Enter appointment number" required>
        </div>
      </div>

      <div class="text-center">
        <button type="submit" class="btn btn-custom me-2">Submit</button>
        <button type="reset" class="btn btn-reset">Reset</button>
      </div>
    </form>
  </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
</body>
</html>
