import { Component } from '@angular/core';
import { Appointment } from '../appointment'; // Adjust path as per your project structure
import { AppointmentService } from '../appointment.service'; // Adjust path as per your project structure
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-appointment',
  templateUrl: './create-appointment.component.html',
  styleUrls: ['./create-appointment.component.css']
})
export class CreateAppointmentComponent {

  appointment: Appointment = new Appointment();

  constructor(private appointmentService: AppointmentService, private router: Router) { }

  onSubmit() {
    this.saveAppointment();
  }

  saveAppointment() {
    this.appointmentService.createAppointment(this.appointment).subscribe(data => {
      console.log(data);
      this.goToAppointmentList();
    });
  }

  goToAppointmentList() {
    this.router.navigate(['/appointmentlist']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Add New Appointment</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <style>
    body {
      background-color: #eef2f5;
    }
    .navbar {
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    }
    .form-container {
      background-color: #ffffff;
      border-radius: 12px;
      box-shadow: 0 6px 12px rgba(0, 0, 0, 0.1);
      padding: 30px;
      margin-top: 50px;
    }
    .form-group label {
      font-weight: bold;
      color: #495057;
    }
    .form-control {
      border: 1px solid #ced4da;
      border-radius: 8px;
    }
    .input-group-text {
      background-color: #f8f9fa;
      border-radius: 8px;
      border: 1px solid #ced4da;
    }
    .btn-custom {
      background-color: #007bff;
      border-color: #007bff;
      color: #ffffff;
      transition: background-color 0.3s ease;
    }
    .btn-custom:hover {
      background-color: #0056b3;
      border-color: #004085;
    }
    .btn-reset {
      background-color: #ffc107;
      border-color: #ffc107;
      color: #212529;
      transition: background-color 0.3s ease;
    }
    .btn-reset:hover {
      background-color: #e0a800;
      border-color: #d39e00;
    }
    .form-title {
      font-weight: 600;
      color: #343a40;
      margin-bottom: 20px;
    }
    .form-container .row .col-md-6 {
      padding-top: 10px;
    }
  </style>
</head>
<body>

<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
  <a class="navbar-brand" href="#">WAGH HOSPITALS</a>
  <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarSupportedContent">
    <ul class="navbar-nav me-auto mb-2 mb-lg-0">
      <li class="nav-item">
        <a class="nav-link" href="javascript:void(0);" routerLink="/admin">Back</a>
      </li>
    </ul>
    <div class="d-flex">
      <button class="btn btn-danger" data-bs-toggle="modal" data-bs-target="#loginModal">
        <a class="text-white" href="javascript:void(0);" routerLink="/admin">Logout</a>
      </button>
    </div>
  </div>
</nav>

<div class="container">
  <div class="form-container mx-auto w-75">
    <h2 class="text-center form-title">Add New Appointment</h2>
    <form (ngSubmit)="onSubmit()" novalidate>
      <div class="row">
        <div class="col-md-6">
          <div class="form-group">
            <label for="name">Patient Name</label>
            <div class="input-group">
              <span class="input-group-text"><i class="fas fa-user"></i></span>
              <input type="text" class="form-control" id="name" name="name" placeholder="Enter patient's name" [(ngModel)]="appointment.name" required>
            </div>
          </div>
        </div>
        <div class="col-md-6">
          <div class="form-group">
            <label for="doctorName">Doctor Name</label>
            <div class="input-group">
              <span class="input-group-text"><i class="fas fa-stethoscope"></i></span>
              <input type="text" class="form-control" id="doctorName" name="doctorName" placeholder="Enter doctor's name" [(ngModel)]="appointment.doctorName" required>
            </div>
          </div>
        </div>
      </div>
      <div class="row">
        <div class="col-md-6">
          <div class="form-group">
            <label for="age">Age</label>
            <div class="input-group">
              <span class="input-group-text"><i class="fas fa-calendar-alt"></i></span>
              <input type="number" class="form-control" id="age" name="age" placeholder="Enter age" [(ngModel)]="appointment.age" required>
            </div>
          </div>
        </div>
        <div class="col-md-6">
          <div class="form-group">
            <label for="mobile">Mobile</label>
            <div class="input-group">
              <span class="input-group-text"><i class="fas fa-phone"></i></span>
              <input type="tel" class="form-control" id="mobile" name="mobile" placeholder="Enter mobile number" [(ngModel)]="appointment.mobile" required>
            </div>
          </div>
        </div>
      </div>
      <div class="mb-3">
        <div class="form-group">
          <label for="symptoms">Symptoms</label>
          <div class="input-group">
            <span class="input-group-text"><i class="fas fa-notes-medical"></i></span>
            <input type="text" class="form-control" id="symptoms" name="symptoms" placeholder="Describe symptoms" [(ngModel)]="appointment.symptoms" required>
          </div>
        </div>
      </div>
      <div class="mb-3">
        <div class="form-group">
          <label for="appointmentNumber">Appointment Number</label>
          <div class="input-group">
            <span class="input-group-text"><i class="fas fa-hashtag"></i></span>
            <input type="text" class="form-control" id="appointmentNumber" name="appointmentNumber" placeholder="Enter appointment number" [(ngModel)]="appointment.appointmentNumber" required>
          </div>
        </div>
      </div>
      <div class="text-center">
        <button type="submit" class="btn btn-custom mx-2">Submit</button>
        <button type="reset" class="btn btn-reset mx-2">Reset</button>
      </div>
    </form>
  </div>
</div>

<script src="https://kit.fontawesome.com/a076d05399.js" crossorigin="anonymous"></script>
<script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.11.7/dist/umd/popper.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.min.js"></script>
</body>
</html>
