import { Component, OnInit } from '@angular/core';
import { PatientService } from '../patient.service';
import { Patient } from '../patient';

@Component({
  selector: 'app-doctordash',
  templateUrl: './doctordash.component.html',
  styleUrls: ['./doctordash.component.css']
})
export class DoctordashComponent implements OnInit {

  patients: Patient[] = [];
  patient: Patient = new Patient(); // Declare patient object

  constructor(private patientService: PatientService) {}

  ngOnInit(): void {
    this.getPatients();
  }

  // Fetch the list of patients
  getPatients(): void {
    this.patientService.getPatientList().subscribe(data => {
      this.patients = data;
    }, error => {
      console.error('Error fetching patient list', error);
    });
  }

  // Add or update patient
  onSubmit(): void {
    if (this.patient.id === 0) {
      // Adding a new patient
      this.patient.registerDate = new Date();
      this.patient.updatedDate = null; // Allow null for new patient
    } else {
      // Updating an existing patient
      this.patient.updatedDate = new Date(); // Update date for existing patient
    }

    this.patientService.createPatient(this.patient).subscribe(() => {
      this.getPatients(); // Refresh patient list
      this.clearForm();   // Clear form fields after submission
    }, error => {
      console.error('Error saving patient', error);
    });
  }

  // Function to clear form fields (if needed)
  clearForm(): void {
    this.patient = new Patient(); // Reset the patient object
  }
}

<nav class="navbar navbar-expand-lg bg-dark">
    <a class="navbar-brand text-white" href="#">WAGH HOSPITALS</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
        <ul class="navbar-nav me-auto mb-2 mb-lg-0">
            <li class="nav-item">
                <a class="nav-link text-white active" aria-current="page" href="javascript:void(0);" routerLink="/add-patient">Add Patients</a>
            </li>
            <li class="nav-item">
                <a class="nav-link text-white active" aria-current="page" href="javascript:void(0);" routerLink="/view-medicines">View Medicines</a>
            </li>
        </ul>
        <div class="mx-2">
            <button class="btn btn-danger" style="margin: 5px;">
                <a class="text-white" href="javascript:void(0);" routerLink="/home">Logout</a>
            </button>
        </div>
    </div>
</nav>

<div class="container">
    <hr>
    <h2 class="text-center" style="margin-top: 20px;">Doctor Dashboard</h2>
    <div class="alert alert-secondary" role="alert">
        <marquee behavior="scroll" direction="left">
            Welcome to the Dashboard. Please find the patient list below! Click Add Patient to add patients, and click View Medicines to view & update medicines.
        </marquee>
    </div>
    <hr>

    <h3 class="text-center text-muted">Current Patient List in the Queue</h3>

    <div class="table-responsive">
        <table class="table table-striped table-bordered">
            <thead class="table-dark">
                <tr>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Mobile</th>
                    <th>Blood Group</th>
                    <th>Prescription</th>
                    <th>Dose</th>
                    <th>Gender</th>
                    <!-- <th>Fees</th> -->
                    <th>Urgency</th>
                    <th>Register Date</th>
                    <th>Updated Date</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let patient of patients">
                    <td>{{ patient.id }}</td>
                    <td>{{ patient.name }}</td>
                    <td>{{ patient.age }}</td>
                    <td>{{ patient.mobile }}</td>
                    <td>{{ patient.blood }}</td>
                    <td>{{ patient.prescription }}</td>
                    <td>{{ patient.dose }}</td>
                    <td>{{ patient.gender }}</td>
                    <td>{{ patient.urgency }}</td>
                    <td>{{ patient.registerDate | date: 'd MMMM, yyyy, h:mm a' }}</td>
                    <td>{{ patient.updatedDate ? (patient.updatedDate | date: 'MMMM d, yyyy, h:mm a') : '-' }}</td>
                    <td>
                <!-- <button (click)="delete(patient.id)" class="btn btn-danger">Delete</button> -->
                <div class="d-flex justify-content-between">
                        <button class="btn btn-primary btn-sm me-2">Update</button>
                        <button class="btn btn-danger btn-sm">Delete</button>
                </div>
                    </td>
                </tr>
            </tbody>
        </table>
    </div>
</div>
