import { Component } from '@angular/core';
import { PatientService } from '../patient.service';
import { Patient } from '../patient';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-patient',
  templateUrl: './create-patient.component.html',
  styleUrls: ['./create-patient.component.css']
})
export class CreatePatientComponent {

  patient: Patient = new Patient();

  constructor(private patientService: PatientService, private router: Router) {

  }

  savePatient(){
    this.patientService.createPatient(this.patient).subscribe(data => {
      console.log(data);
      this.goToPatientList();
    })
  }

  onSubmit(){
  this.savePatient();
  }

  goToPatientList(){
    this.router.navigate(['/doctordash']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Add New Patient</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css">
  <style>
    body {
      background: linear-gradient(to right, #f9f9f9, #e0e0e0);
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    }
    .form-container {
      background: #ffffff;
      border-radius: 12px;
      box-shadow: 0 12px 24px rgba(0, 0, 0, 0.1);
      padding: 40px;
      margin: 20px auto;
      max-width: 900px;
      position: relative;
      z-index: 1;
    }
    .form-container::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 123, 255, 0.1);
      border-radius: 12px;
      z-index: -1;
      transform: rotate(-2deg);
    }
    .form-group label {
      font-weight: 600;
      color: #333;
    }
    .form-group input,
    .form-group select,
    .form-group textarea {
      border-radius: 8px;
      border: 1px solid #ced4da;
      padding: 12px;
      transition: border-color 0.3s ease;
    }
    .form-group input:focus,
    .form-group select:focus,
    .form-group textarea:focus {
      border-color: #007bff;
      box-shadow: 0 0 0 0.2rem rgba(0, 123, 255, 0.25);
    }
    .btn-custom {
      background-color: #007bff;
      border: none;
      color: #ffffff;
      border-radius: 50px;
      padding: 12px 24px;
      font-size: 16px;
      font-weight: 600;
      transition: background-color 0.3s ease, box-shadow 0.3s ease;
    }
    .btn-custom:hover {
      background-color: #0056b3;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
    }
    .btn-reset {
      background-color: #6c757d;
      border: none;
      color: #ffffff;
      border-radius: 50px;
      padding: 12px 24px;
      font-size: 16px;
      font-weight: 600;
      transition: background-color 0.3s ease, box-shadow 0.3s ease;
    }
    .btn-reset:hover {
      background-color: #5a6268;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
    }
    .icon {
      margin-right: 8px;
    }
    .text-center h2 {
      color: #343a40;
      font-weight: 700;
      margin-bottom: 20px;
      font-size: 2rem;
    }
    .form-row {
      margin-bottom: 20px;
    }
  </style>
</head>
<body>

<div class="container mt-5 mb-5">
  <div class="form-container">
    <h2 class="text-center mb-4">Add New Patient</h2>
    <form (ngSubmit)="onSubmit()">
      <div class="row">
        <!-- Name -->
        <div class="form-group col-md-6">
          <label for="name"><i class="fas fa-user icon"></i> Name</label>
          <input type="text" class="form-control" id="name" name="name" placeholder="Enter patient's name" [(ngModel)]="patient.name" required />
        </div>
        <!-- Age -->
        <div class="form-group col-md-6">
          <label for="age"><i class="fas fa-calendar-alt icon"></i> Age</label>
          <input type="number" class="form-control" id="age" name="age" placeholder="Enter patient's age" [(ngModel)]="patient.age" required />
        </div>
      </div>

      <div class="row">
        <!-- Mobile -->
        <div class="form-group col-md-6">
          <label for="mobile"><i class="fas fa-phone icon"></i> Mobile</label>
          <input type="tel" class="form-control" id="mobile" name="mobile" placeholder="Enter patient's mobile number" [(ngModel)]="patient.mobile" required />
        </div>
        <!-- Blood Group -->
        <div class="form-group col-md-6">
          <label for="blood"><i class="fas fa-tint icon"></i> Blood Group</label>
          <select class="form-control" id="blood" name="blood" [(ngModel)]="patient.blood" required>
            <option value="">Select Blood Group</option>
            <option value="A+">A+</option>
            <option value="A-">A-</option>
            <option value="B+">B+</option>
            <option value="B-">B-</option>
            <option value="AB+">AB+</option>
            <option value="AB-">AB-</option>
            <option value="O+">O+</option>
            <option value="O-">O-</option>
          </select>
        </div>
      </div>

      <div class="row">

        <!-- Urgency -->
        <div class="form-group col-md-6">
            <label for="urgency"><i class="fas fa-exclamation-triangle icon"></i> Urgency Level</label>
            <select class="form-control" id="urgency" name="urgency" [(ngModel)]="patient.urgency" required>
              <option value="">Select Urgency Level</option>
              <option value="Normal">Normal</option>
              <option value="High">High</option>
              <option value="Critical">Critical</option>
            </select>
          </div>
        <!-- Dose -->
        <div class="form-group col-md-6">
          <label for="dose"><i class="fas fa-pills icon"></i> Dose</label>
          <select class="form-control" id="dose" name="dose" [(ngModel)]="patient.dose" required>
            <option value="">Select Dose Level</option>
            <option value="1st">1st</option>
            <option value="2nd">2nd</option>
            <option value="3rd">3rd</option>
            <option value="Other">Other</option>
          </select>
        </div>
      </div>

      <div class="row">
        <!-- Gender -->
        <div class="form-group col-md-6">
          <label for="gender"><i class="fas fa-venus-mars icon"></i> Gender</label>
          <select class="form-control" id="gender" name="gender" [(ngModel)]="patient.gender" required>
            <option value="">Select Gender</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
        </div>
        <!-- Fees -->
        <div class="form-group col-md-6">
          <label for="fees"><i class="fas fa-money-bill-wave icon"></i> Fees</label>
          <input type="number" class="form-control" id="fees" name="fees" placeholder="Enter fees" [(ngModel)]="patient.fees" required />
        </div>
      </div>

      <div class="row mb-4">


      </div>

      <div class="text-center">
        <button type="submit" class="btn btn-custom btn-lg mt-3"><i class="fas fa-check-circle"></i> Submit</button>
