import { Component } from '@angular/core';
import { PatientService } from '../patient.service';
import { Patient } from '../patient';
import { Router } from '@angular/router';

@Component({
  selector: 'app-create-patient',
  templateUrl: './create-patient.component.html',
  styleUrls: ['./create-patient.component.css']
})
export class CreatePatientComponent {

  patient: Patient = new Patient();

  constructor(private patientService: PatientService, private router: Router) {

  }

  savePatient(){
    this.patientService.createPatient(this.patient).subscribe(data => {
      console.log(data);
      this.goToPatientList();
    })
  }

  onSubmit(){
  this.savePatient();
  }

  goToPatientList(){
    this.router.navigate(['/doctordash']);
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Add New Patient</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
  <style>
    body {
      background-color: #f8f9fa;
    }
    .form-container {
      background-color: #ffffff;
      border-radius: 10px;
      box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
      padding: 30px;
    }
    .form-group label {
      font-weight: bold;
    }
    .btn-custom {
      background-color: #007bff;
      border-color: #007bff;
      color: #ffffff;
    }
    .btn-custom:hover {
      background-color: #0056b3;
      border-color: #004085;
    }
    .btn-reset {
      background-color: #ffc107;
      border-color: #ffc107;
      color: #212529;
    }
    .btn-reset:hover {
      background-color: #e0a800;
      border-color: #d39e00;
    }
    .text-muted {
      color: #6c757d !important;
    }
    .text-center h2 {
      color: #343a40;
      font-weight: 600;
    }
    .table-form td {
      vertical-align: middle;
    }
    .table-form input, .table-form select, .table-form textarea {
      width: 100%;
    }
  </style>
</head>
<body>

<div class="container mt-5 mb-5">
    <h2 class="text-center mb-4">Add New Patient</h2>
    <form (ngSubmit)="onSubmit()" class="form-container mx-auto">
        <table class="table table-borderless table-form">
            <tbody>
                <tr>
                    <td><label for="name"><i class="fas fa-user"></i> Name</label></td>
                    <td><input type="text" class="form-control" id="name" name="name" placeholder="Enter patient's name" [(ngModel)]="patient.name" required /></td>
                </tr>
                <tr>
                    <td><label for="age"><i class="fas fa-calendar-alt"></i> Age</label></td>
                    <td><input type="number" class="form-control" id="age" name="age" placeholder="Enter patient's age" [(ngModel)]="patient.age" required /></td>
                </tr>
                <tr>
                    <td><label for="mobile"><i class="fas fa-phone"></i> Mobile</label></td>
                    <td><input type="tel" class="form-control" id="mobile" name="mobile" placeholder="Enter patient's mobile number" [(ngModel)]="patient.mobile" required /></td>
                </tr>
                <tr>
                    <td><label for="blood"><i class="fas fa-tint"></i> Blood Group</label></td>
                    <td>
                        <select class="form-control" id="blood" name="blood" [(ngModel)]="patient.blood" required>
                            <option value="">Select Blood Group</option>
                            <option value="A+">A+</option>
                            <option value="A-">A-</option>
                            <option value="B+">B+</option>
                            <option value="B-">B-</option>
                            <option value="AB+">AB+</option>
                            <option value="AB-">AB-</option>
                            <option value="O+">O+</option>
                            <option value="O-">O-</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td><label for="prescription"><i class="fas fa-file-prescription"></i> Prescription</label></td>
                    <td><textarea class="form-control" id="prescription" name="prescription" placeholder="Enter prescription details" [(ngModel)]="patient.prescription" rows="2" required></textarea></td>
                </tr>
                <tr>
                    <td><label for="dose"><i class="fas fa-pills"></i> Dose</label></td>
                    <td>
                        <select class="form-control" id="dose" name="dose" [(ngModel)]="patient.dose" required>
                            <option value="">Select Dose Level</option>
                            <option value="1st">1st</option>
                            <option value="2nd">2nd</option>
                            <option value="3rd">3rd</option>
                            <option value="Other">Other</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td><label for="gender"><i class="fas fa-venus-mars"></i> Gender</label></td>
                    <td>
                        <select class="form-control" id="gender" name="gender" [(ngModel)]="patient.gender" required>
                            <option value="">Select Gender</option>
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                            <option value="Other">Other</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td><label for="fees"><i class="fas fa-money-bill-wave"></i> Fees</label></td>
                    <td><input type="number" class="form-control" id="fees" name="fees" placeholder="Enter fees" [(ngModel)]="patient.fees" required /></td>
                </tr>
                <tr>
                    <td><label for="urgency"><i class="fas fa-exclamation-triangle"></i> Urgency Level</label></td>
                    <td>
                        <select class="form-control" id="urgency" name="urgency" [(ngModel)]="patient.urgency" required>
                            <option value="">Select Urgency Level</option>
                            <option value="Normal">Normal</option>
                            <option value="High">High</option>
                            <option value="Critical">Critical</option>
                        </select>
                    </td>
                </tr>
            </tbody>
        </table>
        <div class="text-center">
            <button type="submit" class="btn btn-success btn-lg mt-3"><i class="fas fa-check-circle"></i> Submit</button>
            <button type="reset" class="btn btn-danger btn-lg mt-3 ml-2"><i class="fas fa-redo-alt"></i> Reset</button>
        </div>
    </form>
</div>

<!-- Font Awesome for icons -->
<script src="https://kit.fontawesome.com/a076d05399.js" crossorigin="anonymous"></script>
<script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
</body>
</html>
